import aoccore.Day

object DaySix extends Day {
  override def number: Int = 6

  override def fileName: String = ???

  override def partOne(): Unit = {
    println(findMarker(4, "mjqjpqmgbljsphdztnvjfqwrcgsmlb"))
    println(findMarker(4, "bvwbjplbgvbhsrlpgdmjqwftvncz"))
    println(findMarker(4, ""))
  }

  private def findMarker(threshold: Int, s: String): Option[Int] = {
    for ((_, i) <- s.toCharArray.zipWithIndex) {
      if (i > threshold - 1) {
        val sub = s.substring(i - threshold, i).toSet
        if (sub.size == threshold) {
          return Some(i)
        }
      }
    }
    None
  }

  override def partTwo(): Unit = {
    println(findMarker(14, "mjqjpqmgbljsphdztnvjfqwrcgsmlb"))
    println(findMarker(14, "bvwbjplbgvbhsrlpgdmjqwftvncz"))
    println(findMarker(14, ""))
  }
}
